contracts/MarketPlace.sol.content:
// SPDX-License-Identifier: MIT
//pragma solidity ^0.8.9;
//pragma solidity =0.6.12;
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract MarketPlace {
    using Counters for Counters.Counter;

    Counters.Counter private _itemCounter;

    struct Item {
        bool listing;
        address nft;
        uint256 id;
        uint256 price;
        address seller;
    }

    // nft => id => Item
    mapping(uint256 => Item) public items;
    IERC20 public immutable payToken;

    event Listed(
        address indexed nft,
        uint256 indexed id,
        address indexed seller,
        uint256 price
    );
    event Canceled(
        address indexed nft,
        uint256 indexed id,
        address indexed seller,
        uint256 price
    );
    event Bought(
        address indexed nft,
        uint256 indexed id,
        address indexed buyer,
        uint256 price
    );

    constructor(IERC20 _paytoken) public {
        payToken = _paytoken;
    }

    function listNFT(
        address nft,
        uint256 id,
        uint256 price
    ) public {
        uint256 itemCounter = _itemCounter.current();
        _itemCounter.increment();
        IERC721(nft).transferFrom(msg.sender, address(this), id);
        items[itemCounter] = Item({
            listing: true,
            nft: nft,
            id: id,
            price: price,
            seller: msg.sender
        });
        emit Listed(nft, id, msg.sender, price);
    }

    function cancelListing(uint256 itemCounter) public {
        Item memory item = items[itemCounter];
        require(msg.sender == item.seller && item.listing, "!seller");
        IERC721(item.nft).transferFrom(address(this), item.seller, item.id);
        Item storage _item = items[itemCounter];
        _item.listing = false;
        emit Canceled(item.nft, item.id, item.seller, item.price);
    }

    function buy(uint256 itemCounter, uint256 amount) public {
        Item memory item = items[itemCounter];
        require(item.nft != address(0) && item.listing, "!listed");
        require(amount >= item.price, "invavlid amount");
        Item storage _item = items[itemCounter];
        _item.listing = false;
        payToken.transferFrom(msg.sender, item.seller, amount);
        IERC721(item.nft).transferFrom(address(this), msg.sender, item.id);
        emit Bought(item.nft, item.id, msg.sender, amount);
    }

    function getListedItems() public view returns (Item[] memory) {
        uint256 itemCounter = _itemCounter.current();
        Item[] memory _items = new Item[](itemCounter);
        for (uint i = 0; i < itemCounter; i++) {
            Item memory item = items[i];
            if (item.listing) {
                _items[i] = item;
            }
        }
        return _items;
    }
}

contracts/DAOPairCreator.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity =0.6.12;

import "./interfaces/IDAOPairCreator.sol";
import "./amm/interfaces/IUniswapV2Factory.sol";
import "./amm/interfaces/IUniswapV2Router02.sol";
import "./amm/libraries/TransferHelper.sol";

contract DAOPairCreator is IDAOPairCreator {
    IUniswapV2Factory public factory;
    IUniswapV2Router02 public router;
    address public admin;
    address public USDP;
    address public rubyStaker;
    uint256 public minimumUnlockedBalance = 0;
    uint256 public minimumLockedBalance = 100000 ether;

    constructor(
        address _admin,
        address _factory,
        address _router,
        address _usdp,
        address _rubyStaker
    ) public {
        require(_admin != address(0), "DAOPairCreator: INVALID_INIT_ARG");
        require(_factory != address(0), "DAOPairCreator: INVALID_INIT_ARG");
        require(_router != address(0), "DAOPairCreator: INVALID_INIT_ARG");
        require(_usdp != address(0), "DAOPairCreator: INVALID_INIT_ARG");
        admin = _admin;
        factory = IUniswapV2Factory(_factory);
        router = IUniswapV2Router02(_router);
        USDP = _usdp;
        rubyStaker = _rubyStaker;
    }

    function createPair(address tokenA, address tokenB) external override returns (address pair) {
        authorized(msg.sender);
        require(tokenA == USDP || tokenB == USDP, "DAOPairCreator: INVALID_TOKEN");
        pair = factory.createPair(tokenA, tokenB);
    }

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        override
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        )
    {
        authorized(msg.sender);
        require(tokenA == USDP || tokenB == USDP, "DAOPairCreator: INVALID_TOKEN");
        TransferHelper.safeTransferFrom(tokenA, msg.sender, address(this), amountADesired);
        TransferHelper.safeTransferFrom(tokenB, msg.sender, address(this), amountBDesired);
        TransferHelper.safeApprove(tokenA, address(router), amountADesired);
        TransferHelper.safeApprove(tokenB, address(router), amountBDesired);
        (amountA, amountB, liquidity) = router.addLiquidity(
            tokenA,
            tokenB,
            amountADesired,
            amountBDesired,
            amountAMin,
            amountBMin,
            to,
            deadline
        );
        if (amountADesired > amountA)
            TransferHelper.safeTransfer(tokenA, msg.sender, amountADesired - amountA);
        if (amountBDesired > amountB)
            TransferHelper.safeTransfer(tokenB, msg.sender, amountBDesired - amountB);
        TransferHelper.safeApprove(tokenA, address(router), 0);
        TransferHelper.safeApprove(tokenB, address(router), 0);
    }

    function setAdmin(address newAdmin) external override {
        require(msg.sender == admin, "DAOPairCreator: FORBIDDEN");
        require(newAdmin != address(0), "DAOPairCreator: INVALID_INIT_ARG");
        admin = newAdmin;
    }

    function setMinimumBalanceRequired(uint256 unlocked, uint256 locked) external override {
        require(msg.sender == admin, "DAOPairCreator: FORBIDDEN");
        minimumUnlockedBalance = unlocked;
        minimumLockedBalance = locked;
    }

    function authorized(address user) public view override returns (bool) {
        (bool success0, bytes memory unlockedData) = rubyStaker.staticcall(
            abi.encodeWithSignature("unlockedBalance(address)", user)
        );
        require(success0, "DAOPairCreator: INVALID_CALL");
        uint256 unlocked = abi.decode(unlockedData, (uint256));
        (bool success1, bytes memory lockedData) = rubyStaker.staticcall(
            abi.encodeWithSignature("lockedBalances(address)", user)
        );
        require(success1, "DAOPairCreator: INVALID_CALL");
        uint256 locked = abi.decode(lockedData, (uint256));
        require(unlocked >= minimumUnlockedBalance, "DAOPairCreator: INSUFFICIENT UNLOCKED RUBY");
        require(locked >= minimumLockedBalance, "DAOPairCreator: INSUFFICIENT LOCKED RUBY");
        return true;
    }
}

